    generateRequest(params) {
        let url = "http://api.football-data.org/v1";
        
        switch(params.request_type) {
            case "competitions": 
                url += "/competitions";
                (typeof params.season !== "undefined") ? url += `?season=${params.season}` : ""; 
            break;
            case "results":
                url += `/competitions/${params.competition_id}/fixtures`;
                (typeof params.matchday !== "undefined") ? url += `?matchday=${params.matchday}` : ""; 
            break;
            case "ranking":
                url += `/competitions/${params.competition_id}/leagueTable`;
                (typeof params.matchday !== "undefined") ? url += `?matchday=${params.matchday}` : ""; 
            break;
            case "teams_in_championship":
                url += `/competitions/${params.competition_id}/teams`;
            break;
            case "team_roster": 
                url += `/teams/${params.team_id}/players`;
            break;
            case "team_info": 
                url += `/teams/${params.team_id}`;
            break;     
            case "competition_info": 
                url += `/competitions/${params.competition_id}/`
            break;
        }
        
        return url;
    }

    getCountryByCompetitionId(competition_id) {
        let result = [];
        
        for(let country of this.info) {
            if(Object.keys(country.competitions).indexOf(competition_id) >= 0) { // If the competition's ID is in the "competitions" JSON structure 
                return country;
            }
        }
        return -1; 
    }
    
    getCountryById(country_id) {
        for(let country of this.info) {
            if(country.country_id === country_id) {
                return country;
            }
        }
        
        return -1;
    }
    
    getCountryList() {
        let countryList = [];
        for(let country of this.info) {
            countryList.push({"id": country.country_id, "name": country.name, "country_text": country.id});
        }
        return countryList;
    }
    
    getCompetitionsByCountryId(country_id) {
        let country = this.getCountryById(country_id),
            result = [];
        if(country === -1) {
            return -1;
        } 
        
        Object.keys(country.competitions).forEach((key) => {
            result.push(country.competitions[key].id);
        });    
        
        return result;
    }
    
